-- DONNÉES GISCOP84 - Échantillon du PDF page 2
-- 32 expositions aux pesticides en viticulture
-- ============================================

USE giscop_pestimat_simple;


-- Statistiques
SELECT
    'GISCOP84 Exposures' AS table_name,
    COUNT(*) AS nb_lignes,
    COUNT(DISTINCT patient_num) AS nb_patients,
    MIN(YEAR(ddebexp)) AS annee_min,
    MAX(YEAR(dfinexp)) AS annee_max
FROM GISCOP84_Exposures;
